import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";
import cors from "cors";

const app = express();

declare module "http" {
  interface IncomingMessage {
    rawBody: unknown;
  }
}

// CORS - Allow requests from anywhere (development only)
app.use(cors());

app.use(
  express.json({
    verify: (req, _res, buf) => {
      req.rawBody = buf;
    },
  }),
);
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || "5000", 10);
  server.listen(
    {
      port,
      host: "0.0.0.0",
      reusePort: true,
    },
    () => {
      log(`serving on port ${port}`);
    },
  );
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth } from "./auth";
import { 
  insertCategorySchema,
  insertSubcategorySchema,
  insertProductSchema, 
  insertOrderSchema,
  insertDeliverySchema,
  insertAssemblySchema,
  insertMaintenanceRecordSchema
} from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // Setup authentication (creates /api/register, /api/login, /api/logout, /api/user, /api/forgot-password)
  setupAuth(app);

  // Reset password endpoint
  app.post("/api/reset-password", async (req, res) => {
    try {
      const user = req.user as any;
      if (!user) {
        return res.status(401).json({ message: "Unauthorized" });
      }

      const resetPasswordSchema = z.object({
        currentPassword: z.string().min(1, "Current password is required"),
        newPassword: z.string().min(6, "New password must be at least 6 characters"),
      });

      const validation = resetPasswordSchema.safeParse(req.body);
      if (!validation.success) {
        return res.status(400).json({ 
          message: "Invalid input", 
          errors: validation.error.errors 
        });
      }

      const { currentPassword, newPassword } = validation.data;

      const { hashPassword, comparePasswords } = await import("./auth");
      const dbUser = await storage.getUserByUsername(user.username);

      if (!dbUser) {
        return res.status(404).json({ message: "User not found" });
      }

      // comparePasswords(plain, hash) - correct order
      const isValid = await comparePasswords(currentPassword, dbUser.password);
      if (!isValid) {
        return res.status(400).json({ message: "Current password is incorrect" });
      }

      const hashedPassword = await hashPassword(newPassword);
      await storage.updateUser(user.id, { password: hashedPassword });

      res.json({ message: "Password reset successfully" });
    } catch (error) {
      console.error("Reset password error:", error);
      res.status(500).json({ message: "Failed to reset password" });
    }
  });

  // Notifications endpoints
  app.get("/api/notifications", async (req, res) => {
    try {
      const user = req.user as any;
      if (!user) {
        return res.status(401).json({ message: "Unauthorized" });
      }

      // Mock notifications for now
      const notifications = [
        {
          id: "1",
          title: "Order Confirmed",
          message: "Your order #ORD-001 has been confirmed",
          type: "success",
          read: false,
          created_at: new Date().toISOString(),
        },
        {
          id: "2",
          title: "New Product Available",
          message: "Check out our new products in the catalog",
          type: "info",
          read: true,
          created_at: new Date(Date.now() - 86400000).toISOString(),
        },
      ];

      res.json(notifications);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch notifications" });
    }
  });

  app.patch("/api/notifications/:id/read", async (req, res) => {
    try {
      const { id } = req.params;
      res.json({ message: "Notification marked as read" });
    } catch (error) {
      res.status(500).json({ message: "Failed to mark notification as read" });
    }
  });

  app.delete("/api/notifications/:id", async (req, res) => {
    try {
      const { id } = req.params;
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ message: "Failed to delete notification" });
    }
  });

  app.patch("/api/notifications/mark-all-read", async (req, res) => {
    try {
      res.json({ message: "All notifications marked as read" });
    } catch (error) {
      res.status(500).json({ message: "Failed to mark all notifications as read" });
    }
  });

  // Products routes
  app.get("/api/products", async (req, res) => {
    try {
      const products = await storage.getAllProducts();
      res.json(products);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch products" });
    }
  });

  app.get("/api/products/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const product = await storage.getProductById(id);
      if (!product) {
        return res.status(404).json({ message: "Product not found" });
      }
      res.json(product);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch product" });
    }
  });

  app.post("/api/products", async (req, res) => {
    try {
      const productData = insertProductSchema.parse(req.body);
      const product = await storage.createProduct(productData);
      res.status(201).json(product);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid product data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to create product" });
      }
    }
  });

  app.put("/api/products/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const productData = insertProductSchema.parse(req.body);
      const product = await storage.updateProduct(id, productData);
      if (!product) {
        return res.status(404).json({ message: "Product not found" });
      }
      res.json(product);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid product data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to update product" });
      }
    }
  });

  app.delete("/api/products/:id", async (req, res) => {
    try {
      const { id } = req.params;
      await storage.deleteProduct(id);
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ message: "Failed to delete product" });
    }
  });

  // Categories routes
  app.get("/api/categories", async (req, res) => {
    try {
      const categories = await storage.getAllCategories();
      res.json(categories);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch categories" });
    }
  });

  app.post("/api/categories", async (req, res) => {
    try {
      const categoryData = insertCategorySchema.parse(req.body);
      const category = await storage.createCategory(categoryData);
      res.status(201).json(category);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid category data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to create category" });
      }
    }
  });

  app.put("/api/categories/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const categoryData = insertCategorySchema.parse(req.body);
      const category = await storage.updateCategory(id, categoryData);
      if (!category) {
        return res.status(404).json({ message: "Category not found" });
      }
      res.json(category);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid category data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to update category" });
      }
    }
  });

  app.delete("/api/categories/:id", async (req, res) => {
    try {
      const { id } = req.params;
      await storage.deleteCategory(id);
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ message: "Failed to delete category" });
    }
  });

  // Subcategories routes
  app.get("/api/subcategories", async (req, res) => {
    try {
      const categoryId = req.query.categoryId as string | undefined;
      const subcategories = categoryId 
        ? await storage.getSubcategoriesByCategoryId(categoryId)
        : await storage.getAllSubcategories();
      res.json(subcategories);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch subcategories" });
    }
  });

  app.post("/api/subcategories", async (req, res) => {
    try {
      const subcategoryData = insertSubcategorySchema.parse(req.body);
      const subcategory = await storage.createSubcategory(subcategoryData);
      res.status(201).json(subcategory);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid subcategory data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to create subcategory" });
      }
    }
  });

  app.put("/api/subcategories/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const subcategoryData = insertSubcategorySchema.parse(req.body);
      const subcategory = await storage.updateSubcategory(id, subcategoryData);
      if (!subcategory) {
        return res.status(404).json({ message: "Subcategory not found" });
      }
      res.json(subcategory);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid subcategory data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to update subcategory" });
      }
    }
  });

  app.delete("/api/subcategories/:id", async (req, res) => {
    try {
      const { id } = req.params;
      await storage.deleteSubcategory(id);
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ message: "Failed to delete subcategory" });
    }
  });

  // Orders routes
  app.get("/api/orders", async (req, res) => {
    try {
      const user = req.user as any;
      const limit = req.query.limit ? parseInt(req.query.limit as string) : undefined;

      // If customer, only return their own orders
      if (user && user.role === "customer") {
        const allOrders = await storage.getAllOrders(limit);
        const userOrders = allOrders.filter(order => order.user_id === user.id);
        res.json(userOrders);
      } else {
        // Admin sees all orders
        const orders = await storage.getAllOrders(limit);
        res.json(orders);
      }
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch orders" });
    }
  });

  app.get("/api/orders/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const order = await storage.getOrderById(id);
      if (!order) {
        return res.status(404).json({ message: "Order not found" });
      }
      res.json(order);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch order" });
    }
  });

  app.post("/api/orders", async (req, res) => {
    try {
      const orderData = insertOrderSchema.parse(req.body);
      const order = await storage.createOrder(orderData);
      res.status(201).json(order);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid order data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to create order" });
      }
    }
  });

  app.put("/api/orders/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const orderData = insertOrderSchema.parse(req.body);
      const order = await storage.updateOrder(id, orderData);
      if (!order) {
        return res.status(404).json({ message: "Order not found" });
      }
      res.json(order);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid order data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to update order" });
      }
    }
  });

  app.patch("/api/orders/:id/status", async (req, res) => {
    try {
      const user = req.user as any;
      if (!user || user.role !== "admin") {
        return res.status(403).json({ message: "Admin access required" });
      }

      const { id } = req.params;
      const { status } = req.body;

      if (!status) {
        return res.status(400).json({ message: "Status is required" });
      }

      const validStatuses = ["pending", "paid", "processing", "completed", "failed", "cancelled"];
      if (!validStatuses.includes(status)) {
        return res.status(400).json({ message: "Invalid status value" });
      }

      const order = await storage.updateOrderStatus(id, status);
      if (!order) {
        return res.status(404).json({ message: "Order not found" });
      }

      res.json(order);
    } catch (error) {
      res.status(500).json({ message: "Failed to update order status" });
    }
  });

  // Checkout endpoint
  app.post("/api/checkout", async (req, res) => {
    try {
      const user = req.user as any;
      if (!user) {
        return res.status(401).json({ message: "Unauthorized" });
      }

      const { product_id, quantity, total_amount, payment_method, payment_amount, payment_transfer_id, payment_transfer_date, customer_info } = req.body;

      if (!product_id || !quantity || !total_amount) {
        return res.status(400).json({ message: "Product ID, quantity, and total amount are required" });
      }

      // Get product to verify it exists
      const product = await storage.getProductById(product_id);
      if (!product) {
        return res.status(404).json({ message: "Product not found" });
      }

      // Generate order number
      const orderNumber = `ORD-${Date.now()}-${Math.random().toString(36).substr(2, 9).toUpperCase()}`;

      // Create order
      const orderData = {
        order_number: orderNumber,
        user_id: user.id,
        total_amount: total_amount.toString(),
        items_count: quantity,
        status: payment_transfer_id ? "paid" : "pending",
        payment_method: payment_method || null,
        payment_amount: payment_amount ? payment_amount.toString() : null,
        payment_transfer_id: payment_transfer_id || null,
        payment_transfer_date: payment_transfer_date ? new Date(payment_transfer_date) : null,
        customer_info: customer_info || null,
      };

      const order = await storage.createOrder(orderData);

      // Create order item
      const orderItemData = {
        order_id: order.id,
        product_id: product_id,
        quantity: quantity,
        price: product.price,
      };

      await storage.createOrderItem(orderItemData);

      res.status(201).json(order);
    } catch (error) {
      console.error("Checkout error:", error);
      res.status(500).json({ message: "Failed to process checkout" });
    }
  });

  // Deliveries routes
  app.get("/api/deliveries", async (req, res) => {
    try {
      const deliveries = await storage.getAllDeliveries();
      res.json(deliveries);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch deliveries" });
    }
  });

  app.post("/api/deliveries", async (req, res) => {
    try {
      const deliveryData = insertDeliverySchema.parse(req.body);
      const delivery = await storage.createDelivery(deliveryData);
      res.status(201).json(delivery);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid delivery data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to create delivery" });
      }
    }
  });

  app.put("/api/deliveries/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const deliveryData = insertDeliverySchema.parse(req.body);
      const delivery = await storage.updateDelivery(id, deliveryData);
      if (!delivery) {
        return res.status(404).json({ message: "Delivery not found" });
      }
      res.json(delivery);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid delivery data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to update delivery" });
      }
    }
  });

  // Assemblies routes
  app.get("/api/assemblies", async (req, res) => {
    try {
      const assemblies = await storage.getAllAssemblies();
      res.json(assemblies);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch assemblies" });
    }
  });

  app.post("/api/assemblies", async (req, res) => {
    try {
      const assemblyData = insertAssemblySchema.parse(req.body);
      const assembly = await storage.createAssembly(assemblyData);
      res.status(201).json(assembly);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid assembly data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to create assembly" });
      }
    }
  });

  app.put("/api/assemblies/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const assemblyData = insertAssemblySchema.parse(req.body);
      const assembly = await storage.updateAssembly(id, assemblyData);
      if (!assembly) {
        return res.status(404).json({ message: "Assembly not found" });
      }
      res.json(assembly);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid assembly data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to update assembly" });
      }
    }
  });

  // Helper to create API schema for maintenance records that accepts date strings
  const createMaintenanceApiSchema = () => {
    return insertMaintenanceRecordSchema.extend({
      scheduled_date: z.union([
        z.date(),
        z.string().refine((val) => !isNaN(new Date(val).getTime()), {
          message: "Invalid date string"
        }).transform(val => new Date(val))
      ]),
      completed_date: z.union([
        z.date(),
        z.string().refine((val) => !isNaN(new Date(val).getTime()), {
          message: "Invalid date string"
        }).transform(val => new Date(val)),
        z.null()
      ]).optional(),
    });
  };

  // Maintenance records routes
  app.get("/api/maintenance", async (req, res) => {
    try {
      const records = await storage.getAllMaintenanceRecords();
      res.json(records);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch maintenance records" });
    }
  });

  app.post("/api/maintenance", async (req, res) => {
    try {
      const apiSchema = createMaintenanceApiSchema();
      const recordData = apiSchema.parse(req.body);
      const record = await storage.createMaintenanceRecord(recordData);
      res.status(201).json(record);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid maintenance record data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to create maintenance record" });
      }
    }
  });

  app.put("/api/maintenance/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const apiSchema = createMaintenanceApiSchema();
      const recordData = apiSchema.parse(req.body);
      const record = await storage.updateMaintenanceRecord(id, recordData);
      if (!record) {
        return res.status(404).json({ message: "Maintenance record not found" });
      }
      res.json(record);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid maintenance record data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to update maintenance record" });
      }
    }
  });

  // Dashboard stats
  app.get("/api/stats", async (req, res) => {
    try {
      const stats = await storage.getDashboardStats();
      res.json(stats);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch dashboard stats" });
    }
  });

  // Monitoring - Delivered products with customer details
  app.get("/api/monitoring/delivered", async (req, res) => {
    try {
      const user = req.user as any;
      if (!user || user.role !== "admin") {
        return res.status(403).json({ message: "Admin access required" });
      }

      const deliveredOrders = await storage.getDeliveredOrdersWithDetails();
      res.json(deliveredOrders);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch delivered orders" });
    }
  });

  // Categories with subcategories endpoint
  app.get("/api/categories-with-subcategories", async (req, res) => {
    try {
      const categoriesWithSubs = await storage.getCategoriesWithSubcategories();
      res.json(categoriesWithSubs);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch categories with subcategories" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  users,
  categories,
  subcategories,
  products,
  orders,
  order_items,
  deliveries,
  assemblies,
  maintenance_records,
  type Category,
  type InsertCategory,
  type Subcategory,
  type InsertSubcategory,
  type Product, 
  type InsertProduct,
  type Order,
  type InsertOrder,
  type OrderItem,
  type InsertOrderItem,
  type Delivery,
  type InsertDelivery,
  type Assembly,
  type InsertAssembly,
  type MaintenanceRecord,
  type InsertMaintenanceRecord,
  type User,
  type InsertUser
} from "@shared/schema";
import { db } from "./db";
import { eq, desc } from "drizzle-orm";
import session from "express-session";
import connectPg from "connect-pg-simple";
import { pool } from "./db";

export interface IStorage {
  // Session store
  sessionStore: session.Store;

  // User methods
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: string, data: Partial<InsertUser>): Promise<User | undefined>;

  // Category methods
  getAllCategories(): Promise<Category[]>;
  getCategoryById(id: string): Promise<Category | undefined>;
  createCategory(category: InsertCategory): Promise<Category>;
  updateCategory(id: string, category: InsertCategory): Promise<Category | undefined>;
  deleteCategory(id: string): Promise<void>;
  getCategoriesWithSubcategories(): Promise<any[]>;

  // Subcategory methods
  getAllSubcategories(): Promise<Subcategory[]>;
  getSubcategoriesByCategoryId(categoryId: string): Promise<Subcategory[]>;
  getSubcategoryById(id: string): Promise<Subcategory | undefined>;
  createSubcategory(subcategory: InsertSubcategory): Promise<Subcategory>;
  updateSubcategory(id: string, subcategory: InsertSubcategory): Promise<Subcategory | undefined>;
  deleteSubcategory(id: string): Promise<void>;

  // Product methods
  getAllProducts(): Promise<Product[]>;
  getProductById(id: string): Promise<Product | undefined>;
  createProduct(product: InsertProduct): Promise<Product>;
  updateProduct(id: string, product: InsertProduct): Promise<Product | undefined>;
  deleteProduct(id: string): Promise<void>;

  // Order methods
  getAllOrders(limit?: number): Promise<Order[]>;
  getOrderById(id: string): Promise<Order | undefined>;
  createOrder(order: InsertOrder): Promise<Order>;
  updateOrder(id: string, order: InsertOrder): Promise<Order | undefined>;
  updateOrderStatus(id: string, status: string): Promise<Order | undefined>;
  getDeliveredOrdersWithDetails(): Promise<any[]>;

  // Order item methods
  getOrderItemsByOrderId(orderId: string): Promise<OrderItem[]>;
  createOrderItem(orderItem: InsertOrderItem): Promise<OrderItem>;

  // Delivery methods
  getAllDeliveries(): Promise<Delivery[]>;
  getDeliveryById(id: string): Promise<Delivery | undefined>;
  createDelivery(delivery: InsertDelivery): Promise<Delivery>;
  updateDelivery(id: string, delivery: InsertDelivery): Promise<Delivery | undefined>;

  // Assembly methods
  getAllAssemblies(): Promise<Assembly[]>;
  getAssemblyById(id: string): Promise<Assembly | undefined>;
  createAssembly(assembly: InsertAssembly): Promise<Assembly>;
  updateAssembly(id: string, assembly: InsertAssembly): Promise<Assembly | undefined>;

  // Maintenance methods
  getAllMaintenanceRecords(): Promise<MaintenanceRecord[]>;
  getMaintenanceRecordById(id: string): Promise<MaintenanceRecord | undefined>;
  createMaintenanceRecord(record: InsertMaintenanceRecord): Promise<MaintenanceRecord>;
  updateMaintenanceRecord(id: string, record: InsertMaintenanceRecord): Promise<MaintenanceRecord | undefined>;

  // Dashboard stats
  getDashboardStats(): Promise<{
    totalProducts: number;
    totalOrders: number;
    pendingOrders: number;
    totalRevenue: number;
  }>;
}

const PostgresSessionStore = connectPg(session);

export class DatabaseStorage implements IStorage {
  sessionStore: session.Store;

  constructor() {
    this.sessionStore = new PostgresSessionStore({ 
      pool, 
      createTableIfMissing: true 
    });
  }

  // User methods
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user || undefined;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(insertUser)
      .returning();
    return user;
  }

  async updateUser(id: string, data: Partial<InsertUser>): Promise<User | undefined> {
    const [user] = await db
      .update(users)
      .set(data)
      .where(eq(users.id, id))
      .returning();
    return user || undefined;
  }

  // Category methods
  async getAllCategories(): Promise<Category[]> {
    return await db.select().from(categories).orderBy(categories.name);
  }

  async getCategoryById(id: string): Promise<Category | undefined> {
    const [category] = await db.select().from(categories).where(eq(categories.id, id));
    return category || undefined;
  }

  async createCategory(category: InsertCategory): Promise<Category> {
    const [newCategory] = await db
      .insert(categories)
      .values(category)
      .returning();
    return newCategory;
  }

  async updateCategory(id: string, category: InsertCategory): Promise<Category | undefined> {
    const [updatedCategory] = await db
      .update(categories)
      .set(category)
      .where(eq(categories.id, id))
      .returning();
    return updatedCategory || undefined;
  }

  async deleteCategory(id: string): Promise<void> {
    await db.delete(categories).where(eq(categories.id, id));
  }

  async getCategoriesWithSubcategories(): Promise<any[]> {
    const allCategories = await db.select().from(categories).orderBy(categories.name);
    const allSubcategories = await db.select().from(subcategories).orderBy(subcategories.name);

    return allCategories.map(category => ({
      ...category,
      subcategories: allSubcategories.filter(sub => sub.category_id === category.id)
    }));
  }

  // Subcategory methods
  async getAllSubcategories(): Promise<Subcategory[]> {
    return await db.select().from(subcategories).orderBy(subcategories.name);
  }

  async getSubcategoriesByCategoryId(categoryId: string): Promise<Subcategory[]> {
    return await db.select().from(subcategories).where(eq(subcategories.category_id, categoryId)).orderBy(subcategories.name);
  }

  async getSubcategoryById(id: string): Promise<Subcategory | undefined> {
    const [subcategory] = await db.select().from(subcategories).where(eq(subcategories.id, id));
    return subcategory || undefined;
  }

  async createSubcategory(subcategory: InsertSubcategory): Promise<Subcategory> {
    const [newSubcategory] = await db
      .insert(subcategories)
      .values(subcategory)
      .returning();
    return newSubcategory;
  }

  async updateSubcategory(id: string, subcategory: InsertSubcategory): Promise<Subcategory | undefined> {
    const [updatedSubcategory] = await db
      .update(subcategories)
      .set(subcategory)
      .where(eq(subcategories.id, id))
      .returning();
    return updatedSubcategory || undefined;
  }

  async deleteSubcategory(id: string): Promise<void> {
    await db.delete(subcategories).where(eq(subcategories.id, id));
  }

  // Product methods
  async getAllProducts(): Promise<Product[]> {
    return await db.select().from(products).orderBy(products.name);
  }

  async getProductById(id: string): Promise<Product | undefined> {
    const [product] = await db.select().from(products).where(eq(products.id, id));
    return product || undefined;
  }

  async createProduct(product: InsertProduct): Promise<Product> {
    const [newProduct] = await db
      .insert(products)
      .values(product)
      .returning();
    return newProduct;
  }

  async updateProduct(id: string, product: InsertProduct): Promise<Product | undefined> {
    const [updatedProduct] = await db
      .update(products)
      .set(product)
      .where(eq(products.id, id))
      .returning();
    return updatedProduct || undefined;
  }

  async deleteProduct(id: string): Promise<void> {
    await db.delete(products).where(eq(products.id, id));
  }

  // Order methods
  async getAllOrders(limit?: number): Promise<Order[]> {
    let query = db.select().from(orders).orderBy(desc(orders.created_at));

    if (limit) {
      query = query.limit(limit) as any;
    }

    return await query;
  }

  async getOrderById(id: string): Promise<Order | undefined> {
    const [order] = await db.select().from(orders).where(eq(orders.id, id));
    return order || undefined;
  }

  async createOrder(order: InsertOrder): Promise<Order> {
    const [newOrder] = await db
      .insert(orders)
      .values(order)
      .returning();
    return newOrder;
  }

  async updateOrder(id: string, order: InsertOrder): Promise<Order | undefined> {
    const [updatedOrder] = await db
      .update(orders)
      .set(order)
      .where(eq(orders.id, id))
      .returning();
    return updatedOrder || undefined;
  }

  async updateOrderStatus(id: string, status: string): Promise<Order | undefined> {
    const [updatedOrder] = await db
      .update(orders)
      .set({ status, updated_at: new Date() })
      .where(eq(orders.id, id))
      .returning();
    return updatedOrder || undefined;
  }

  async getDeliveredOrdersWithDetails(): Promise<any[]> {
    const deliveredOrders = await db
      .select({
        order_id: orders.id,
        order_number: orders.order_number,
        total_amount: orders.total_amount,
        items_count: orders.items_count,
        payment_method: orders.payment_method,
        created_at: orders.created_at,
        customer_info: orders.customer_info,
        user_id: orders.user_id,
        username: users.username,
        email: users.email,
      })
      .from(orders)
      .leftJoin(users, eq(orders.user_id, users.id))
      .where(eq(orders.status, 'completed'))
      .orderBy(desc(orders.created_at));

    return deliveredOrders;
  }

  // Order item methods
  async getOrderItemsByOrderId(orderId: string): Promise<OrderItem[]> {
    return await db.select().from(order_items).where(eq(order_items.order_id, orderId));
  }

  async createOrderItem(orderItem: InsertOrderItem): Promise<OrderItem> {
    const [newOrderItem] = await db
      .insert(order_items)
      .values(orderItem)
      .returning();
    return newOrderItem;
  }

  // Delivery methods
  async getAllDeliveries(): Promise<Delivery[]> {
    return await db.select().from(deliveries).orderBy(desc(deliveries.created_at));
  }

  async getDeliveryById(id: string): Promise<Delivery | undefined> {
    const [delivery] = await db.select().from(deliveries).where(eq(deliveries.id, id));
    return delivery || undefined;
  }

  async createDelivery(delivery: InsertDelivery): Promise<Delivery> {
    const [newDelivery] = await db
      .insert(deliveries)
      .values(delivery)
      .returning();
    return newDelivery;
  }

  async updateDelivery(id: string, delivery: InsertDelivery): Promise<Delivery | undefined> {
    const [updatedDelivery] = await db
      .update(deliveries)
      .set(delivery)
      .where(eq(deliveries.id, id))
      .returning();
    return updatedDelivery || undefined;
  }

  // Assembly methods
  async getAllAssemblies(): Promise<Assembly[]> {
    return await db.select().from(assemblies).orderBy(desc(assemblies.created_at));
  }

  async getAssemblyById(id: string): Promise<Assembly | undefined> {
    const [assembly] = await db.select().from(assemblies).where(eq(assemblies.id, id));
    return assembly || undefined;
  }

  async createAssembly(assembly: InsertAssembly): Promise<Assembly> {
    const [newAssembly] = await db
      .insert(assemblies)
      .values(assembly)
      .returning();
    return newAssembly;
  }

  async updateAssembly(id: string, assembly: InsertAssembly): Promise<Assembly | undefined> {
    const [updatedAssembly] = await db
      .update(assemblies)
      .set(assembly)
      .where(eq(assemblies.id, id))
      .returning();
    return updatedAssembly || undefined;
  }

  // Maintenance methods
  async getAllMaintenanceRecords(): Promise<MaintenanceRecord[]> {
    return await db.select().from(maintenance_records).orderBy(desc(maintenance_records.created_at));
  }

  async getMaintenanceRecordById(id: string): Promise<MaintenanceRecord | undefined> {
    const [record] = await db.select().from(maintenance_records).where(eq(maintenance_records.id, id));
    return record || undefined;
  }

  async createMaintenanceRecord(record: InsertMaintenanceRecord): Promise<MaintenanceRecord> {
    const [newRecord] = await db
      .insert(maintenance_records)
      .values(record)
      .returning();
    return newRecord;
  }

  async updateMaintenanceRecord(id: string, record: InsertMaintenanceRecord): Promise<MaintenanceRecord | undefined> {
    const [updatedRecord] = await db
      .update(maintenance_records)
      .set(record)
      .where(eq(maintenance_records.id, id))
      .returning();
    return updatedRecord || undefined;
  }

  // Dashboard stats
  async getDashboardStats(): Promise<{
    totalProducts: number;
    totalOrders: number;
    pendingOrders: number;
    totalRevenue: number;
  }> {
    const [productCount] = await db.select().from(products);
    const allOrders = await db.select().from(orders);

    const totalProducts = await db.select().from(products).then(rows => rows.length);
    const totalOrders = allOrders.length;
    const pendingOrders = allOrders.filter(order => 
      ['pending', 'paid', 'processing'].includes(order.status)
    ).length;
    const totalRevenue = allOrders.reduce((sum, order) => 
      sum + parseFloat(order.total_amount), 0
    );

    return {
      totalProducts,
      totalOrders,
      pendingOrders,
      totalRevenue
    };
  }
}

export const storage = new DatabaseStorage();

