  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";

export default function Categories() {
  const [showCategoryForm, setShowCategoryForm] = useState(false);
  const [showSubcategoryForm, setShowSubcategoryForm] = useState(false);
  const [editingCategory, setEditingCategory] = useState<Category | null>(null);
  const [editingSubcategory, setEditingSubcategory] = useState<Subcategory | null>(null);
  const [selectedCategoryForSubcategory, setSelectedCategoryForSubcategory] = useState<string | null>(null);
  const [categoryToDelete, setCategoryToDelete] = useState<Category | null>(null);
  const [subcategoryToDelete, setSubcategoryToDelete] = useState<Subcategory | null>(null);

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: categories = [], isLoading: categoriesLoading } = useQuery<Category[]>({
    queryKey: ["/api/categories"],
  });

  const { data: subcategories = [] } = useQuery<Subcategory[]>({
    queryKey: ["/api/subcategories"],
  });

  const createCategoryMutation = useMutation({
    mutationFn: async (data: any) => {
      return await apiRequest("POST", "/api/categories", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/categories"] });
      setShowCategoryForm(false);
      toast({
        title: "Category created",
        description: "The category has been created successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create category.",
        variant: "destructive",
      });
    },
  });

  const updateCategoryMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: any }) => {
      return await apiRequest("PUT", `/api/categories/${id}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/categories"] });
      setShowCategoryForm(false);
      setEditingCategory(null);
      toast({
        title: "Category updated",
        description: "The category has been updated successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update category.",
        variant: "destructive",
      });
    },
  });

  const deleteCategoryMutation = useMutation({
    mutationFn: async (id: string) => {
      return await apiRequest("DELETE", `/api/categories/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/categories"] });
      queryClient.invalidateQueries({ queryKey: ["/api/subcategories"] });
      setCategoryToDelete(null);
      toast({
        title: "Category deleted",
        description: "The category has been deleted successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete category.",
        variant: "destructive",
      });
    },
  });

  const createSubcategoryMutation = useMutation({
    mutationFn: async (data: any) => {
      return await apiRequest("POST", "/api/subcategories", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/subcategories"] });
      setShowSubcategoryForm(false);
      setSelectedCategoryForSubcategory(null);
      toast({
        title: "Subcategory created",
        description: "The subcategory has been created successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create subcategory.",
        variant: "destructive",
      });
    },
  });

  const updateSubcategoryMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: any }) => {
      return await apiRequest("PUT", `/api/subcategories/${id}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/subcategories"] });
      setShowSubcategoryForm(false);
      setEditingSubcategory(null);
      toast({
        title: "Subcategory updated",
        description: "The subcategory has been updated successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update subcategory.",
        variant: "destructive",
      });
    },
  });

  const deleteSubcategoryMutation = useMutation({
    mutationFn: async (id: string) => {
      return await apiRequest("DELETE", `/api/subcategories/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/subcategories"] });
      setSubcategoryToDelete(null);
      toast({
        title: "Subcategory deleted",
        description: "The subcategory has been deleted successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete subcategory.",
        variant: "destructive",
      });
    },
  });

  const handleCreateCategory = () => {
    setEditingCategory(null);
    setShowCategoryForm(true);
  };

  const handleEditCategory = (category: Category) => {
    setEditingCategory(category);
    setShowCategoryForm(true);
  };

  const handleCategorySubmit = (data: any) => {
    if (editingCategory) {
      updateCategoryMutation.mutate({ id: editingCategory.id, data });
    } else {
      createCategoryMutation.mutate(data);
    }
  };

  const handleAddSubcategory = (categoryId: string) => {
    setEditingSubcategory(null);
    setSelectedCategoryForSubcategory(categoryId);
    setShowSubcategoryForm(true);
  };

  const handleEditSubcategory = (subcategory: Subcategory) => {
    setEditingSubcategory(subcategory);
    setSelectedCategoryForSubcategory(subcategory.category_id);
    setShowSubcategoryForm(true);
  };

  const handleSubcategorySubmit = (data: any) => {
    if (editingSubcategory) {
      updateSubcategoryMutation.mutate({ id: editingSubcategory.id, data });
    } else {
      createSubcategoryMutation.mutate(data);
    }
  };

  const getSubcategoriesForCategory = (categoryId: string) => {
    return subcategories.filter((sub) => sub.category_id === categoryId);
  };

  if (categoriesLoading) {
    return <div className="p-8">Loading categories...</div>;
  }

  return (
    <div className="p-8" data-testid="categories-page">
      <div className="mb-8 flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold flex items-center gap-2">
            <FolderTree className="h-8 w-8" />
            Categories Management
          </h1>
          <p className="text-muted-foreground mt-2">
            Manage product categories and subcategories
          </p>
        </div>
        <Button onClick={handleCreateCategory} data-testid="button-add-category">
          <Plus className="h-4 w-4 mr-2" />
          Add Category
        </Button>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {categories.map((category) => {
          const categorySubcategories = getSubcategoriesForCategory(category.id);
          return (
            <Card key={category.id} data-testid={`category-card-${category.id}`}>
              <CardHeader className="pb-3">
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <CardTitle className="text-lg">{category.name}</CardTitle>
                    {category.description && (
                      <p className="text-sm text-muted-foreground mt-1">
                        {category.description}
                      </p>
                    )}
                  </div>
                  <div className="flex gap-1">
                    <Button
                      variant="ghost"
                      size="icon"
                      onClick={() => handleEditCategory(category)}
                      data-testid={`button-edit-category-${category.id}`}
                    >
                      <Edit className="h-4 w-4" />
                    </Button>
                    <Button
                      variant="ghost"
                      size="icon"
                      onClick={() => setCategoryToDelete(category)}
                      data-testid={`button-delete-category-${category.id}`}
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              </CardHeader>
              <CardContent>
                <div className="space-y-2">
                  <div className="flex items-center justify-between">
                    <span className="text-sm font-medium">
                      Subcategories ({categorySubcategories.length})
                    </span>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => handleAddSubcategory(category.id)}
                      data-testid={`button-add-subcategory-${category.id}`}
                    >
                      <Plus className="h-3 w-3 mr-1" />
                      Add
                    </Button>
                  </div>
                  <div className="space-y-1">
                    {categorySubcategories.map((subcategory) => (
                      <div
                        key={subcategory.id}
                        className="flex items-center justify-between p-2 rounded-md bg-muted/50 hover:bg-muted"
                        data-testid={`subcategory-item-${subcategory.id}`}
                      >
                        <span className="text-sm">{subcategory.name}</span>
                        <div className="flex gap-1">
                          <Button
                            variant="ghost"
                            size="icon"
                            className="h-6 w-6"
                            onClick={() => handleEditSubcategory(subcategory)}
                            data-testid={`button-edit-subcategory-${subcategory.id}`}
                          >
                            <Edit className="h-3 w-3" />
                          </Button>
                          <Button
                            variant="ghost"
                            size="icon"
                            className="h-6 w-6"
                            onClick={() => setSubcategoryToDelete(subcategory)}
                            data-testid={`button-delete-subcategory-${subcategory.id}`}
                          >
                            <Trash2 className="h-3 w-3" />
                          </Button>
                        </div>
                      </div>
                    ))}
                    {categorySubcategories.length === 0 && (
                      <p className="text-sm text-muted-foreground text-center py-2">
                        No subcategories yet
                      </p>
                    )}
                  </div>
                </div>
              </CardContent>
            </Card>
          );
        })}
      </div>

      {categories.length === 0 && (
        <Card>
          <CardContent className="flex flex-col items-center justify-center py-12">
            <FolderTree className="h-12 w-12 text-muted-foreground mb-4" />
            <p className="text-lg font-medium mb-2">No categories yet</p>
            <p className="text-sm text-muted-foreground mb-4">
              Get started by creating your first category
            </p>
            <Button onClick={handleCreateCategory}>
              <Plus className="h-4 w-4 mr-2" />
              Add Category
            </Button>
          </CardContent>
        </Card>
      )}

      {showCategoryForm && (
        <CategoryForm
          category={editingCategory}
          onSubmit={handleCategorySubmit}
          onCancel={() => {
            setShowCategoryForm(false);
            setEditingCategory(null);
          }}
          isLoading={createCategoryMutation.isPending || updateCategoryMutation.isPending}
        />
      )}

      {showSubcategoryForm && (
        <SubcategoryForm
          subcategory={editingSubcategory}
          categories={categories}
          initialCategoryId={selectedCategoryForSubcategory}
          onSubmit={handleSubcategorySubmit}
          onCancel={() => {
            setShowSubcategoryForm(false);
            setEditingSubcategory(null);
            setSelectedCategoryForSubcategory(null);
          }}
          isLoading={createSubcategoryMutation.isPending || updateSubcategoryMutation.isPending}
        />
      )}

      <AlertDialog open={!!categoryToDelete} onOpenChange={() => setCategoryToDelete(null)}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>Delete Category</AlertDialogTitle>
            <AlertDialogDescription>
              Are you sure you want to delete "{categoryToDelete?.name}"? This will also delete all
              subcategories under this category. This action cannot be undone.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel data-testid="button-cancel-delete-category">Cancel</AlertDialogCancel>
            <AlertDialogAction
              onClick={() => categoryToDelete && deleteCategoryMutation.mutate(categoryToDelete.id)}
              className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
              data-testid="button-confirm-delete-category"
            >
              Delete
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>

      <AlertDialog open={!!subcategoryToDelete} onOpenChange={() => setSubcategoryToDelete(null)}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>Delete Subcategory</AlertDialogTitle>
            <AlertDialogDescription>
              Are you sure you want to delete "{subcategoryToDelete?.name}"? This action cannot be undone.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel data-testid="button-cancel-delete-subcategory">Cancel</AlertDialogCancel>
            <AlertDialogAction
              onClick={() => subcategoryToDelete && deleteSubcategoryMutation.mutate(subcategoryToDelete.id)}
              className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
              data-testid="button-confirm-delete-subcategory"
            >
              Delete
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </div>
  );
}

client/src/pages/Dashboard.tsx.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { useAuth } from "@/hooks/use-auth";
import StatsGrid from "@/components/dashboard/StatsGrid";
import RecentOrders from "@/components/dashboard/RecentOrders";
import RevenueChart from "@/components/dashboard/RevenueChart";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { Zap, PlusCircle, Activity, Wrench, AlertTriangle, Package, Clock, CheckCircle2, ShoppingCart, Lock } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import type { Order } from "@shared/schema";
import { useState } from "react";

export default function Dashboard() {
  const { user } = useAuth();
  const isCustomer = user?.role === "customer";

  const { data: stats, isLoading: statsLoading } = useQuery({
    queryKey: ["/api/stats"],
    enabled: !isCustomer, // Only fetch admin stats for non-customers
  });

  const { data: orders = [], isLoading: ordersLoading } = useQuery<Order[]>({
    queryKey: ["/api/orders"],
  });

  // Customer-specific stats
  const customerOrders = user ? orders.filter(order => order.user_id === user.id) : [];
  const pendingOrders = customerOrders.filter(order => order.status === "pending").length;
  const completedOrders = customerOrders.filter(order => order.status === "completed").length;
  const totalSpent = customerOrders.reduce((sum, order) => sum + Number(order.total_amount), 0);

  // Reset password state and mutation
  const [currentPassword, setCurrentPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const { toast } = useToast();

  const resetPasswordMutation = useMutation({
    mutationFn: async (data: { currentPassword: string; newPassword: string }) =>
      apiRequest("POST", "/api/reset-password", data),
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Your password has been reset successfully",
      });
      setCurrentPassword("");
      setNewPassword("");
      setConfirmPassword("");
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to reset password",
        variant: "destructive",
      });
    },
  });

  const handleResetPassword = (e: React.FormEvent) => {
    e.preventDefault();

    if (newPassword !== confirmPassword) {
      toast({
        title: "Error",
        description: "New passwords do not match",
        variant: "destructive",
      });
      return;
    }

    if (newPassword.length < 6) {
      toast({
        title: "Error",
        description: "Password must be at least 6 characters long",
        variant: "destructive",
      });
      return;
    }

    resetPasswordMutation.mutate({
      currentPassword,
      newPassword,
    });
  };

  if (isCustomer) {
    return (
      <div className="space-y-8">
        {/* Customer Welcome Section */}
        <div className="flex flex-col gap-4 md:flex-row md:items-center md:justify-between">
          <div>
            <h2 className="mb-2 text-3xl font-bold text-foreground">Welcome back, {user?.username}!</h2>
            <p className="text-muted-foreground">
              Here's an overview of your orders and activity.
            </p>
          </div>
        </div>

        {/* Customer Stats */}
        <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-4">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Total Orders</CardTitle>
              <ShoppingCart className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold" data-testid="customer-total-orders">{customerOrders.length}</div>
            </CardContent>
          </Card>
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Pending Orders</CardTitle>
              <Clock className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold" data-testid="customer-pending-orders">{pendingOrders}</div>
            </CardContent>
          </Card>
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Completed Orders</CardTitle>
              <CheckCircle2 className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold" data-testid="customer-completed-orders">{completedOrders}</div>
            </CardContent>
          </Card>
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Total Spent</CardTitle>
              <Package className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold" data-testid="customer-total-spent">${totalSpent.toFixed(2)}</div>
            </CardContent>
          </Card>
        </div>

        {/* Customer Recent Orders */}
        <Card>
          <CardHeader>
            <CardTitle>My Recent Orders</CardTitle>
          </CardHeader>
          <CardContent>
            <RecentOrders orders={customerOrders} isLoading={ordersLoading} />
          </CardContent>
        </Card>

        {/* Reset Password */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Lock className="h-5 w-5" />
              Reset Password
            </CardTitle>
            <CardDescription>Change your account password</CardDescription>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleResetPassword} className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="current-password">Current Password</Label>
                <Input
                  id="current-password"
                  type="password"
                  value={currentPassword}
                  onChange={(e) => setCurrentPassword(e.target.value)}
                  required
                  data-testid="input-current-password"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="new-password">New Password</Label>
                <Input
                  id="new-password"
                  type="password"
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                  required
                  data-testid="input-new-password"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="confirm-password">Confirm New Password</Label>
                <Input
                  id="confirm-password"
                  type="password"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                  required
                  data-testid="input-confirm-password"
                />
              </div>
              <Button
                type="submit"
                disabled={resetPasswordMutation.isPending}
                data-testid="button-reset-password"
              >
                {resetPasswordMutation.isPending ? "Resetting..." : "Reset Password"}
              </Button>
            </form>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Admin Welcome Section */}
      <div className="flex flex-col gap-4 md:flex-row md:items-center md:justify-between">
        <div>
          <h2 className="mb-2 text-3xl font-bold text-foreground">Welcome back, {user?.username || "Admin"}!</h2>
          <p className="text-muted-foreground">
            Here's what's happening with your vending machine network today.
          </p>
        </div>
        <div className="flex items-center gap-2 rounded-full bg-green-50 px-4 py-2">
          <div className="h-3 w-3 animate-pulse rounded-full bg-green-500"></div>
          <span className="text-sm font-medium text-green-800">All Systems Operational</span>
        </div>
      </div>

      {/* Stats Grid */}
      <StatsGrid stats={stats} isLoading={statsLoading} />

      {/* Main Content Grid */}
      <div className="grid gap-8 lg:grid-cols-3">
        {/* Left Column - Charts and Analytics */}
        <div className="space-y-8 lg:col-span-2">
          <RevenueChart />
        </div>

        {/* Right Column - Recent Activities */}
        <div className="space-y-8">
          <RecentOrders orders={orders} isLoading={ordersLoading} />

          {/* Quick Actions */}
          <Card className="glass-effect border-0 shadow-xl" data-testid="quick-actions-card">
            <CardHeader className="pb-4">
              <CardTitle className="flex items-center gap-3">
                <div className="flex h-10 w-10 items-center justify-center rounded-xl bg-gradient-to-br from-purple-500 to-pink-500">
                  <Zap className="h-5 w-5 text-white" />
                </div>
                Quick Actions
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-3">
              <Button className="h-12 w-full justify-start gap-3 bg-gradient-to-r from-blue-500 to-blue-600 hover:from-blue-600 hover:to-blue-700" data-testid="add-product-button">
                <PlusCircle className="h-4 w-4" />
                Add New Product
              </Button>
              <Button variant="outline" className="h-12 w-full justify-start gap-3" data-testid="health-check-button">
                <Activity className="h-4 w-4" />
                System Health Check
              </Button>
              <Button variant="outline" className="h-12 w-full justify-start gap-3" data-testid="schedule-maintenance-button">
                <Wrench className="h-4 w-4" />
                Schedule Maintenance
              </Button>
            </CardContent>
          </Card>

          {/* Alerts */}
          <Card className="glass-effect border-0 border-l-4 border-l-orange-500 shadow-xl" data-testid="alerts-card">
            <CardHeader className="pb-4">
              <CardTitle className="flex items-center gap-3 text-orange-800">
                <AlertTriangle className="h-5 w-5" />
                System Alerts
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-3">
              <div className="flex items-start gap-3 rounded-lg bg-orange-50 p-3" data-testid="alert-low-stock">
                <div className="mt-2 h-2 w-2 flex-shrink-0 rounded-full bg-orange-500"></div>
                <div>
                  <p className="text-sm font-medium text-orange-900">Low Stock Alert</p>
                  <p className="text-xs text-orange-700">
                    Some products need restocking (12 items remaining)
                  </p>
                  <p className="mt-1 text-xs text-orange-600">5 minutes ago</p>
                </div>
              </div>
              <div className="flex items-start gap-3 rounded-lg bg-red-50 p-3" data-testid="alert-maintenance">
                <div className="mt-2 h-2 w-2 flex-shrink-0 rounded-full bg-red-500"></div>
                <div>
                  <p className="text-sm font-medium text-red-900">Maintenance Due</p>
                  <p className="text-xs text-red-700">
                    Equipment requires immediate service
                  </p>
                  <p className="mt-1 text-xs text-red-600">1 hour ago</p>
                </div>
              </div>
              <div className="flex items-start gap-3 rounded-lg bg-yellow-50 p-3" data-testid="alert-payment">
                <div className="mt-2 h-2 w-2 flex-shrink-0 rounded-full bg-yellow-500"></div>
                <div>
                  <p className="text-sm font-medium text-yellow-900">Payment Gateway Issue</p>
                  <p className="text-xs text-yellow-700">
                    Payment processing delayed
                  </p>
                  <p className="mt-1 text-xs text-yellow-600">2 hours ago</p>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

client/src/pages/Deliveries.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { PlusCircle, Download } from "lucide-react";
import DeliveriesTable from "@/components/deliveries/DeliveriesTable";
import DeliveryFilters from "@/components/deliveries/DeliveryFilters";
import TrackingModal from "@/components/deliveries/TrackingModal";
import { Card, CardContent } from "@/components/ui/card";
import { Clock, Truck, CheckCircle, XCircle } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import type { Delivery } from "@shared/schema";

export default function Deliveries() {
  const [selectedDelivery, setSelectedDelivery] = useState<Delivery | null>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: deliveries = [], isLoading } = useQuery<Delivery[]>({
    queryKey: ["/api/deliveries"],
  });

  const updateDeliveryMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: any }) => {
      const response = await apiRequest("PUT", `/api/deliveries/${id}`, data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/deliveries"] });
      toast({
        title: "Success",
        description: "Delivery updated successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update delivery",
        variant: "destructive",
      });
    },
  });

  const deliveryStats = {
    total: deliveries?.length || 0,
    pending: deliveries?.filter((d: Delivery) => d.status === "pending").length || 0,
    in_transit: deliveries?.filter((d: Delivery) => d.status === "in_transit").length || 0,
    delivered: deliveries?.filter((d: Delivery) => d.status === "delivered").length || 0,
    cancelled: deliveries?.filter((d: Delivery) => d.status === "cancelled").length || 0,
  };

  const handleStatusUpdate = (deliveryId: string, status: string) => {
    updateDeliveryMutation.mutate({
      id: deliveryId,
      data: { status }
    });
  };

  return (
    <div className="space-y-6">
      {/* Page Header */}
      <div className="flex flex-col gap-4 md:flex-row md:items-center md:justify-between">
        <div>
          <h2 className="mb-2 text-3xl font-bold text-foreground">Deliveries</h2>
          <p className="text-muted-foreground">Track and manage inventory deliveries</p>
        </div>
        <div className="flex gap-2">
          <Button variant="outline" data-testid="export-deliveries-button">
            <Download className="mr-2 h-4 w-4" />
            Export
          </Button>
          <Button data-testid="schedule-delivery-button">
            <PlusCircle className="mr-2 h-4 w-4" />
            Schedule Delivery
          </Button>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 gap-6 md:grid-cols-4">
        <Card className="glass-effect">
          <CardContent className="p-4">
            <div className="mb-2 flex items-center gap-3">
              <div className="flex h-10 w-10 items-center justify-center rounded-lg bg-blue-100">
                <Truck className="h-5 w-5 text-blue-600" />
              </div>
              <span className="text-sm text-muted-foreground">Total Deliveries</span>
            </div>
            <p className="text-2xl font-bold text-foreground" data-testid="total-deliveries-stat">
              {deliveryStats.total}
            </p>
          </CardContent>
        </Card>

        <Card className="glass-effect">
          <CardContent className="p-4">
            <div className="mb-2 flex items-center gap-3">
              <div className="flex h-10 w-10 items-center justify-center rounded-lg bg-orange-100">
                <Clock className="h-5 w-5 text-orange-600" />
              </div>
              <span className="text-sm text-muted-foreground">Pending</span>
            </div>
            <p className="text-2xl font-bold text-foreground" data-testid="pending-deliveries-stat">
              {deliveryStats.pending}
            </p>
          </CardContent>
        </Card>

        <Card className="glass-effect">
          <CardContent className="p-4">
            <div className="mb-2 flex items-center gap-3">
              <div className="flex h-10 w-10 items-center justify-center rounded-lg bg-blue-100">
                <Truck className="h-5 w-5 text-blue-600" />
              </div>
              <span className="text-sm text-muted-foreground">In Transit</span>
            </div>
            <p className="text-2xl font-bold text-foreground" data-testid="in-transit-deliveries-stat">
              {deliveryStats.in_transit}
            </p>
          </CardContent>
        </Card>

        <Card className="glass-effect">
          <CardContent className="p-4">
            <div className="mb-2 flex items-center gap-3">
              <div className="flex h-10 w-10 items-center justify-center rounded-lg bg-green-100">
                <CheckCircle className="h-5 w-5 text-green-600" />
              </div>
              <span className="text-sm text-muted-foreground">Delivered</span>
            </div>
            <p className="text-2xl font-bold text-foreground" data-testid="delivered-deliveries-stat">
              {deliveryStats.delivered}
            </p>
          </CardContent>
        </Card>
      </div>

      {/* Filters */}
      <DeliveryFilters />

      {/* Deliveries Table */}
      <DeliveriesTable 
        deliveries={deliveries || []} 
        isLoading={isLoading}
        onTrackDelivery={setSelectedDelivery}
        onStatusUpdate={handleStatusUpdate}
      />

      {/* Tracking Modal */}
      {selectedDelivery && (
        <TrackingModal
          delivery={selectedDelivery}
          onClose={() => setSelectedDelivery(null)}
        />
      )}
    </div>
  );
}

