import { useEffect } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { insertAssemblySchema, type Assembly, type InsertAssembly } from "@shared/schema";

interface AssemblyFormProps {
  assembly?: Assembly | null;
  onSubmit: (data: InsertAssembly) => void;
  onCancel: () => void;
  isLoading: boolean;
}

export default function AssemblyForm({ assembly, onSubmit, onCancel, isLoading }: AssemblyFormProps) {
  const form = useForm<InsertAssembly>({
    resolver: zodResolver(insertAssemblySchema),
    defaultValues: {
      name: "",
      type: "component",
      components: {},
      status: "pending",
      priority: "normal",
      assigned_to: "",
      estimated_time: 60,
      notes: "",
    },
  });

  useEffect(() => {
    if (assembly) {
      form.reset({
        name: assembly.name,
        type: assembly.type,
        components: assembly.components,
        status: assembly.status,
        priority: assembly.priority,
        assigned_to: assembly.assigned_to || "",
        estimated_time: assembly.estimated_time || 60,
        notes: assembly.notes || "",
      });
    }
  }, [assembly, form]);

  const handleSubmit = (data: InsertAssembly) => {
    // Parse components JSON if it's a string
    let parsedData = { ...data };
    if (typeof data.components === 'string') {
      try {
        parsedData.components = JSON.parse(data.components);
      } catch {
        // If parsing fails, create a simple object
        parsedData.components = { description: data.components };
      }
    }
    onSubmit(parsedData);
  };

  const componentsString = typeof form.getValues("components") === 'string' 
    ? form.getValues("components") as string
    : JSON.stringify(form.getValues("components"), null, 2);

  return (
    <Dialog open={true} onOpenChange={onCancel}>
      <DialogContent className="max-w-2xl" data-testid="assembly-form-dialog">
        <DialogHeader>
          <DialogTitle>{assembly ? "Edit Assembly Task" : "Create Assembly Task"}</DialogTitle>
          <DialogDescription>
            {assembly ? "Update the assembly task information below." : "Fill in the details for the new assembly task."}
          </DialogDescription>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-6">
            <div className="grid grid-cols-1 gap-4 md:grid-cols-2">
              <FormField
                control={form.control}
                name="name"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Task Name</FormLabel>
                    <FormControl>
                      <Input placeholder="Enter task name" {...field} data-testid="assembly-name-input" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="type"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Type</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value} data-testid="assembly-type-select">
                      <FormControl>
                        <SelectTrigger>
                          <SelectValue placeholder="Select type" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="component">Component</SelectItem>
                        <SelectItem value="kit">Kit</SelectItem>
                        <SelectItem value="full_machine">Full Machine</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="priority"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Priority</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value} data-testid="assembly-priority-select">
                      <FormControl>
                        <SelectTrigger>
                          <SelectValue />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="low">Low</SelectItem>
                        <SelectItem value="normal">Normal</SelectItem>
                        <SelectItem value="high">High</SelectItem>
                        <SelectItem value="urgent">Urgent</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="status"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Status</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value} data-testid="assembly-status-select">
                      <FormControl>
                        <SelectTrigger>
                          <SelectValue />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="pending">Pending</SelectItem>
                        <SelectItem value="in_progress">In Progress</SelectItem>
                        <SelectItem value="completed">Completed</SelectItem>
                        <SelectItem value="cancelled">Cancelled</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="assigned_to"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Assigned To</FormLabel>
                    <FormControl>
                      <Input placeholder="Technician name" {...field} value={field.value || ""} data-testid="assembly-assigned-input" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="estimated_time"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Estimated Time (minutes)</FormLabel>
                    <FormControl>
                      <Input
                        type="number"
                        min="0"
                        placeholder="60"
                        {...field}
                        value={field.value || 0}
                        onChange={(e) => field.onChange(parseInt(e.target.value) || 0)}
                        data-testid="assembly-time-input"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="components"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Components (JSON format)</FormLabel>
                  <FormControl>
                    <Textarea
                      placeholder='{"screws": 4, "panels": 2, "motors": 1}'
                      className="min-h-[100px] font-mono text-sm"
                      value={componentsString}
                      onChange={(e) => field.onChange(e.target.value)}
                      data-testid="assembly-components-input"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="notes"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Notes</FormLabel>
                  <FormControl>
                    <Textarea
                      placeholder="Additional notes..."
                      className="min-h-[80px]"
                      {...field}
                      value={field.value || ""}
                      data-testid="assembly-notes-input"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <DialogFooter>
              <Button type="button" variant="outline" onClick={onCancel} data-testid="assembly-form-cancel">
                Cancel
              </Button>
              <Button type="submit" disabled={isLoading} data-testid="assembly-form-submit">
                {isLoading ? "Saving..." : assembly ? "Update Task" : "Create Task"}
              </Button>
            </DialogFooter>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/assembly/AssemblyT.content:
import { Card } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Skeleton } from "@/components/ui/skeleton";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { MoreVertical, Wrench } from "lucide-react";
import type { Assembly } from "@shared/schema";

interface AssemblyTableProps {
  assemblies: Assembly[];
  isLoading: boolean;
  onEdit: (assembly: Assembly) => void;
}

export default function AssemblyTable({ assemblies, isLoading, onEdit }: AssemblyTableProps) {
  if (isLoading) {
    return (
      <Card className="glass-effect overflow-hidden">
        <div className="overflow-x-auto">
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Task Name</TableHead>
                <TableHead>Type</TableHead>
                <TableHead>Priority</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Assigned To</TableHead>
                <TableHead>Est. Time</TableHead>
                <TableHead>Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {Array.from({ length: 5 }).map((_, i) => (
                <TableRow key={i}>
                  <TableCell><Skeleton className="h-4 w-32" /></TableCell>
                  <TableCell><Skeleton className="h-4 w-20" /></TableCell>
                  <TableCell><Skeleton className="h-4 w-16" /></TableCell>
                  <TableCell><Skeleton className="h-4 w-20" /></TableCell>
                  <TableCell><Skeleton className="h-4 w-24" /></TableCell>
                  <TableCell><Skeleton className="h-4 w-16" /></TableCell>
                  <TableCell><Skeleton className="h-4 w-8" /></TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </div>
      </Card>
    );
  }

  const getStatusBadge = (status: string) => {
    const variants = {
      pending: "outline",
      in_progress: "secondary",
      completed: "default", 
      cancelled: "destructive",
    } as const;

    return (
      <Badge variant={variants[status as keyof typeof variants] || "outline"}>
        {status.replace('_', ' ').charAt(0).toUpperCase() + status.replace('_', ' ').slice(1)}
      </Badge>
    );
  };

  const getPriorityBadge = (priority: string) => {
    const variants = {
      low: "outline",
      normal: "secondary",
      high: "destructive",
      urgent: "destructive",
    } as const;

    return (
      <Badge variant={variants[priority as keyof typeof variants] || "outline"}>
        {priority.charAt(0).toUpperCase() + priority.slice(1)}
      </Badge>
    );
  };

  const formatEstimatedTime = (minutes: number | null) => {
    if (!minutes) return "Not set";
    if (minutes < 60) return `${minutes}m`;
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return mins > 0 ? `${hours}h ${mins}m` : `${hours}h`;
  };

  const formatDate = (date: Date | null) => {
    if (!date) return "N/A";
    const dateObj = date instanceof Date ? date : new Date(date);
    return dateObj.toLocaleDateString();
  };

  if (assemblies.length === 0) {
    return (
      <Card className="glass-effect p-12">
        <div className="text-center">
          <Wrench className="mx-auto mb-4 h-16 w-16 text-muted-foreground" />
          <h3 className="mb-2 text-lg font-semibold">No assembly tasks</h3>
          <p className="text-muted-foreground">Assembly tasks will appear here when created.</p>
        </div>
      </Card>
    );
  }

  return (
    <Card className="glass-effect overflow-hidden" data-testid="assembly-table">
      <div className="overflow-x-auto">
        <Table>
          <TableHeader className="bg-muted/50">
            <TableRow>
              <TableHead className="px-6 py-4 text-xs font-semibold uppercase tracking-wider">
                Task Name
              </TableHead>
              <TableHead className="px-6 py-4 text-xs font-semibold uppercase tracking-wider">
                Type
              </TableHead>
              <TableHead className="px-6 py-4 text-xs font-semibold uppercase tracking-wider">
                Priority
              </TableHead>
              <TableHead className="px-6 py-4 text-xs font-semibold uppercase tracking-wider">
                Status
              </TableHead>
              <TableHead className="px-6 py-4 text-xs font-semibold uppercase tracking-wider">
                Assigned To
              </TableHead>
              <TableHead className="px-6 py-4 text-xs font-semibold uppercase tracking-wider">
                Est. Time
              </TableHead>
              <TableHead className="px-6 py-4 text-xs font-semibold uppercase tracking-wider">
                Created
              </TableHead>
              <TableHead className="px-6 py-4 text-xs font-semibold uppercase tracking-wider">
                Actions
              </TableHead>
            </TableRow>
          </TableHeader>
          <TableBody className="divide-y divide-border">
            {assemblies.map((assembly) => (
              <TableRow
                key={assembly.id}
                className="transition-colors hover:bg-muted/30"
                data-testid={`assembly-row-${assembly.id}`}
              >
                <TableCell className="px-6 py-4">
                  <div>
                    <span className="font-semibold text-foreground">{assembly.name}</span>
                    {assembly.notes && (
                      <p className="text-xs text-muted-foreground mt-1 line-clamp-1">
                        {assembly.notes}
                      </p>
                    )}
                  </div>
                </TableCell>
                <TableCell className="px-6 py-4">
                  <span className="text-sm text-foreground capitalize">
                    {assembly.type.replace('_', ' ')}
                  </span>
                </TableCell>
                <TableCell className="px-6 py-4">
                  {getPriorityBadge(assembly.priority)}
                </TableCell>
                <TableCell className="px-6 py-4">
                  {getStatusBadge(assembly.status)}
                </TableCell>
                <TableCell className="px-6 py-4 text-sm text-foreground">
                  {assembly.assigned_to || "Unassigned"}
                </TableCell>
                <TableCell className="px-6 py-4 text-sm text-muted-foreground">
                  {formatEstimatedTime(assembly.estimated_time)}
                </TableCell>
                <TableCell className="px-6 py-4 text-sm text-muted-foreground">
                  {formatDate(assembly.created_at)}
                </TableCell>
                <TableCell className="px-6 py-4">
                  <Button
                    variant="ghost"
                    size="icon"
                    onClick={() => onEdit(assembly)}
                    data-testid={`edit-assembly-${assembly.id}`}
                  >
                    <MoreVertical className="h-4 w-4" />
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </div>
    </Card>
  );
}

