import { Card } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Skeleton } from "@/components/ui/skeleton";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Eye, MapPin, Truck } from "lucide-react";
import type { Delivery } from "@shared/schema";

interface DeliveriesTableProps {
  deliveries: Delivery[];
  isLoading: boolean;
  onTrackDelivery: (delivery: Delivery) => void;
  onStatusUpdate: (id: string, status: string) => void;
}

export default function DeliveriesTable({ 
  deliveries, 
  isLoading, 
  onTrackDelivery, 
  onStatusUpdate 
}: DeliveriesTableProps) {
  if (isLoading) {
    return (
      <Card className="glass-effect overflow-hidden">
        <div className="overflow-x-auto">
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Delivery #</TableHead>
                <TableHead>Items</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Driver</TableHead>
                <TableHead>Scheduled</TableHead>
                <TableHead>Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {Array.from({ length: 5 }).map((_, i) => (
                <TableRow key={i}>
                  <TableCell><Skeleton className="h-4 w-20" /></TableCell>
                  <TableCell><Skeleton className="h-4 w-12" /></TableCell>
                  <TableCell><Skeleton className="h-4 w-16" /></TableCell>
                  <TableCell><Skeleton className="h-4 w-20" /></TableCell>
                  <TableCell><Skeleton className="h-4 w-24" /></TableCell>
                  <TableCell><Skeleton className="h-4 w-8" /></TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </div>
      </Card>
    );
  }

  const getStatusBadge = (status: string) => {
    const variants = {
      pending: "outline",
      in_transit: "secondary",
      delivered: "default",
      cancelled: "destructive",
    } as const;

    return (
      <Badge variant={variants[status as keyof typeof variants] || "outline"}>
        {status.replace('_', ' ').charAt(0).toUpperCase() + status.replace('_', ' ').slice(1)}
      </Badge>
    );
  };

  const formatDate = (date: Date | null) => {
    if (!date) return "Not scheduled";
    const dateObj = date instanceof Date ? date : new Date(date);
    return dateObj.toLocaleString();
  };

  const getItemsCount = (items: any) => {
    if (!items || typeof items !== 'object') return 0;
    if (Array.isArray(items)) return items.length;
    return Object.keys(items).length;
  };

  if (deliveries.length === 0) {
    return (
      <Card className="glass-effect p-12">
        <div className="text-center">
          <Truck className="mx-auto mb-4 h-16 w-16 text-muted-foreground" />
          <h3 className="mb-2 text-lg font-semibold">No deliveries scheduled</h3>
          <p className="text-muted-foreground">Deliveries will appear here when scheduled.</p>
        </div>
      </Card>
    );
  }

  return (
    <Card className="glass-effect overflow-hidden" data-testid="deliveries-table">
      <div className="overflow-x-auto">
        <Table>
          <TableHeader className="bg-muted/50">
            <TableRow>
              <TableHead className="px-6 py-4 text-xs font-semibold uppercase tracking-wider">
                Delivery #
              </TableHead>
              <TableHead className="px-6 py-4 text-xs font-semibold uppercase tracking-wider">
                Items
              </TableHead>
              <TableHead className="px-6 py-4 text-xs font-semibold uppercase tracking-wider">
                Status
              </TableHead>
              <TableHead className="px-6 py-4 text-xs font-semibold uppercase tracking-wider">
                Driver
              </TableHead>
              <TableHead className="px-6 py-4 text-xs font-semibold uppercase tracking-wider">
                Scheduled
              </TableHead>
              <TableHead className="px-6 py-4 text-xs font-semibold uppercase tracking-wider">
                Actions
              </TableHead>
            </TableRow>
          </TableHeader>
          <TableBody className="divide-y divide-border">
            {deliveries.map((delivery) => (
              <TableRow
                key={delivery.id}
                className="transition-colors hover:bg-muted/30"
                data-testid={`delivery-row-${delivery.delivery_number}`}
              >
                <TableCell className="px-6 py-4">
                  <span className="font-semibold text-foreground">
                    #{delivery.delivery_number}
                  </span>
                </TableCell>
                <TableCell className="px-6 py-4 text-sm text-muted-foreground">
                  {getItemsCount(delivery.items)} items
                </TableCell>
                <TableCell className="px-6 py-4">
                  {getStatusBadge(delivery.status)}
                </TableCell>
                <TableCell className="px-6 py-4 text-sm text-foreground">
                  {delivery.driver_name || "Not assigned"}
                </TableCell>
                <TableCell className="px-6 py-4 text-sm text-muted-foreground">
                  {formatDate(delivery.delivery_date)}
                </TableCell>
                <TableCell className="px-6 py-4">
                  <div className="flex gap-1">
                    <Button
                      variant="ghost"
                      size="icon"
                      onClick={() => onTrackDelivery(delivery)}
                      data-testid={`track-delivery-${delivery.delivery_number}`}
                    >
                      <Eye className="h-4 w-4" />
                    </Button>
                    {delivery.status === "pending" && (
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => onStatusUpdate(delivery.id, "in_transit")}
                        data-testid={`start-delivery-${delivery.delivery_number}`}
                      >
                        Start
                      </Button>
                    )}
                    {delivery.status === "in_transit" && (
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => onStatusUpdate(delivery.id, "delivered")}
                        data-testid={`complete-delivery-${delivery.delivery_number}`}
                      >
                        Complete
                      </Button>
                    )}
                  </div>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </div>
    </Card>
  );
}

client/src/components/deliveries/Trackin.content:
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Separator } from "@/components/ui/separator";
import { MapPin, Truck, Package, User, Clock, CheckCircle } from "lucide-react";
import type { Delivery } from "@shared/schema";

interface TrackingModalProps {
  delivery: Delivery;
  onClose: () => void;
}

export default function TrackingModal({ delivery, onClose }: TrackingModalProps) {
  const getStatusBadge = (status: string) => {
    const variants = {
      pending: "outline",
      in_transit: "secondary",
      delivered: "default",
      cancelled: "destructive",
    } as const;

    return (
      <Badge variant={variants[status as keyof typeof variants] || "outline"}>
        {status.replace('_', ' ').charAt(0).toUpperCase() + status.replace('_', ' ').slice(1)}
      </Badge>
    );
  };

  const formatDateTime = (date: Date | null) => {
    if (!date) return "Not scheduled";
    const dateObj = date instanceof Date ? date : new Date(date);
    return dateObj.toLocaleString();
  };

  const getItemsArray = (items: any) => {
    if (!items) return [];
    if (Array.isArray(items)) return items;
    if (typeof items === 'object') {
      return Object.entries(items).map(([key, value]) => ({
        name: key,
        quantity: value
      }));
    }
    return [];
  };

  const getTrackingInfo = (tracking: any) => {
    if (!tracking || typeof tracking !== 'object') return null;
    return tracking;
  };

  const trackingInfo = getTrackingInfo(delivery.tracking_info);
  const itemsArray = getItemsArray(delivery.items);

  return (
    <Dialog open={true} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl" data-testid="tracking-modal">
        <DialogHeader className="space-y-4">
          <div className="flex items-center justify-between">
            <div>
              <DialogTitle className="text-lg">Delivery Tracking</DialogTitle>
              <DialogDescription>#{delivery.delivery_number}</DialogDescription>
            </div>
            <div className="flex items-center gap-2">
              {getStatusBadge(delivery.status)}
            </div>
          </div>
        </DialogHeader>

        <div className="space-y-6">
          {/* Delivery Summary */}
          <div>
            <h3 className="mb-3 text-sm font-semibold text-foreground">Delivery Information</h3>
            <div className="grid grid-cols-2 gap-4 text-sm">
              <div className="flex items-center gap-2">
                <User className="h-4 w-4 text-muted-foreground" />
                <div>
                  <span className="text-muted-foreground">Driver:</span>
                  <p className="font-medium">{delivery.driver_name || "Not assigned"}</p>
                </div>
              </div>
              <div className="flex items-center gap-2">
                <Clock className="h-4 w-4 text-muted-foreground" />
                <div>
                  <span className="text-muted-foreground">Scheduled:</span>
                  <p className="font-medium">{formatDateTime(delivery.delivery_date)}</p>
                </div>
              </div>
              <div className="flex items-center gap-2">
                <Package className="h-4 w-4 text-muted-foreground" />
                <div>
                  <span className="text-muted-foreground">Items:</span>
                  <p className="font-medium">{itemsArray.length} items</p>
                </div>
              </div>
            </div>
          </div>

          <Separator />

          {/* Items List */}
          <div>
            <h3 className="mb-3 text-sm font-semibold text-foreground">Delivery Items</h3>
            <div className="max-h-32 space-y-2 overflow-y-auto">
              {itemsArray.length > 0 ? (
                itemsArray.map((item, index) => (
                  <div key={index} className="flex justify-between rounded-lg bg-muted/30 p-2 text-sm">
                    <span className="text-foreground">{item.name || `Item ${index + 1}`}</span>
                    <span className="text-muted-foreground">
                      Qty: {item.quantity || 1}
                    </span>
                  </div>
                ))
              ) : (
                <p className="text-muted-foreground">No items specified</p>
              )}
            </div>
          </div>

          <Separator />

          {/* Tracking Timeline */}
          <div>
            <h3 className="mb-3 text-sm font-semibold text-foreground">Tracking Timeline</h3>
            <div className="space-y-3">
              {/* Created */}
              <div className="flex items-start gap-3">
                <div className="mt-1 h-2 w-2 rounded-full bg-blue-500"></div>
                <div className="text-sm">
                  <p className="font-medium">Delivery Scheduled</p>
                  <p className="text-muted-foreground">{formatDateTime(delivery.created_at)}</p>
                </div>
              </div>

              {/* In Transit */}
              {delivery.status !== "pending" && (
                <div className="flex items-start gap-3">
                  <div className="mt-1 h-2 w-2 rounded-full bg-orange-500"></div>
                  <div className="text-sm">
                    <p className="font-medium">Out for Delivery</p>
                    <p className="text-muted-foreground">
                      {trackingInfo?.started_at ? formatDateTime(trackingInfo.started_at) : "Status updated"}
                    </p>
                  </div>
                </div>
              )}

              {/* Delivered */}
              {delivery.status === "delivered" && (
                <div className="flex items-start gap-3">
                  <div className="mt-1 h-2 w-2 rounded-full bg-green-500"></div>
                  <div className="text-sm">
                    <p className="font-medium">Delivered</p>
                    <p className="text-muted-foreground">
                      {trackingInfo?.delivered_at ? formatDateTime(trackingInfo.delivered_at) : formatDateTime(delivery.updated_at)}
                    </p>
                  </div>
                </div>
              )}

              {/* Cancelled */}
              {delivery.status === "cancelled" && (
                <div className="flex items-start gap-3">
                  <div className="mt-1 h-2 w-2 rounded-full bg-red-500"></div>
                  <div className="text-sm">
                    <p className="font-medium">Delivery Cancelled</p>
                    <p className="text-muted-foreground">{formatDateTime(delivery.updated_at)}</p>
                  </div>
                </div>
              )}
            </div>
          </div>

          {/* Notes */}
          {delivery.notes && (
            <>
              <Separator />
              <div>
                <h3 className="mb-2 text-sm font-semibold text-foreground">Notes</h3>
                <p className="text-sm text-muted-foreground">{delivery.notes}</p>
              </div>
            </>
          )}

          {/* Additional Tracking Info */}
          {trackingInfo && Object.keys(trackingInfo).length > 0 && (
            <>
              <Separator />
              <div>
                <h3 className="mb-3 text-sm font-semibold text-foreground">Additional Information</h3>
                <div className="space-y-2 text-sm">
                  {Object.entries(trackingInfo).map(([key, value]) => (
                    <div key={key} className="flex justify-between">
                      <span className="text-muted-foreground capitalize">
                        {key.replace('_', ' ')}:
                      </span>
                      <span className="font-medium">{String(value)}</span>
                    </div>
                  ))}
                </div>
              </div>
            </>
          )}
        </div>

        <div className="flex justify-end">
          <Button onClick={onClose} data-testid="close-tracking-modal">
            Close
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/maintenance/Mainte.content:
import { useEffect } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { insertMaintenanceRecordSchema, type MaintenanceRecord, type InsertMaintenanceRecord } from "@shared/schema";
import { z } from "zod";

const formSchema = insertMaintenanceRecordSchema.extend({
  scheduled_date: z.string(),
});

type FormData = z.infer<typeof formSchema>;

interface MaintenanceFormProps {
  record?: MaintenanceRecord | null;
  onSubmit: (data: InsertMaintenanceRecord) => void;
  onCancel: () => void;
  isLoading: boolean;
}

export default function MaintenanceForm({ record, onSubmit, onCancel, isLoading }: MaintenanceFormProps) {
  const form = useForm<FormData>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      type: "routine",
      priority: "normal",
      status: "scheduled",
      scheduled_date: "",
      technician: "",
      description: "",
      notes: "",
      cost: "",
    },
  });

  useEffect(() => {
    if (record) {
      // Format date for input
      const scheduledDate = new Date(record.scheduled_date);
      const formattedDate = scheduledDate.toISOString().slice(0, 16);

      form.reset({
        type: record.type,
        priority: record.priority,
        status: record.status,
        scheduled_date: formattedDate,
        technician: record.technician || "",
        description: record.description || "",
        notes: record.notes || "",
        cost: record.cost || "",
      });
    }
  }, [record, form]);

  const handleSubmit = (data: FormData) => {
    // Convert the date string to Date object for the backend
    const submissionData: InsertMaintenanceRecord = {
      ...data,
      scheduled_date: new Date(data.scheduled_date),
    };
    onSubmit(submissionData);
  };

  return (
    <Dialog open={true} onOpenChange={onCancel}>
      <DialogContent className="max-w-2xl" data-testid="maintenance-form-dialog">
        <DialogHeader>
          <DialogTitle>{record ? "Edit Maintenance Record" : "Schedule Maintenance"}</DialogTitle>
          <DialogDescription>
            {record ? "Update the maintenance record information below." : "Fill in the details for the new maintenance task."}
          </DialogDescription>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-6">
            <div className="grid grid-cols-1 gap-4 md:grid-cols-2">

              <FormField
                control={form.control}
                name="type"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Type</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value} data-testid="maintenance-type-select">
                      <FormControl>
                        <SelectTrigger>
                          <SelectValue />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="routine">Routine</SelectItem>
                        <SelectItem value="repair">Repair</SelectItem>
                        <SelectItem value="inspection">Inspection</SelectItem>
                        <SelectItem value="cleaning">Cleaning</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="priority"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Priority</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value} data-testid="maintenance-priority-select">
                      <FormControl>
                        <SelectTrigger>
                          <SelectValue />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="low">Low</SelectItem>
                        <SelectItem value="normal">Normal</SelectItem>
                        <SelectItem value="high">High</SelectItem>
                        <SelectItem value="urgent">Urgent</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="status"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Status</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value} data-testid="maintenance-status-select">
                      <FormControl>
                        <SelectTrigger>
                          <SelectValue />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="scheduled">Scheduled</SelectItem>
                        <SelectItem value="in_progress">In Progress</SelectItem>
                        <SelectItem value="completed">Completed</SelectItem>
                        <SelectItem value="cancelled">Cancelled</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="scheduled_date"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Scheduled Date & Time</FormLabel>
                    <FormControl>
                      <Input
                        type="datetime-local"
                        {...field}
                        data-testid="maintenance-date-input"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="technician"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Technician</FormLabel>
                    <FormControl>
                      <Input placeholder="Technician name" {...field} value={field.value || ""} data-testid="maintenance-technician-input" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="cost"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Cost ($)</FormLabel>
                    <FormControl>
                      <Input
                        type="number"
                        step="0.01"
                        min="0"
                        placeholder="0.00"
                        {...field}
                        value={field.value || ""}
                        data-testid="maintenance-cost-input"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="description"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Description</FormLabel>
                  <FormControl>
                    <Textarea
                      placeholder="Describe the maintenance task..."
                      className="min-h-[80px]"
                      {...field}
                      value={field.value || ""}
                      data-testid="maintenance-description-input"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="notes"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Notes</FormLabel>
                  <FormControl>
                    <Textarea
                      placeholder="Additional notes..."
                      className="min-h-[80px]"
                      {...field}
                      value={field.value || ""}
                      data-testid="maintenance-notes-input"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <DialogFooter>
              <Button type="button" variant="outline" onClick={onCancel} data-testid="maintenance-form-cancel">
                Cancel
              </Button>
              <Button type="submit" disabled={isLoading} data-testid="maintenance-form-submit">
                {isLoading ? "Saving..." : record ? "Update Record" : "Schedule Maintenance"}
              </Button>
            </DialogFooter>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

import { Card } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Skeleton } from "@/components/ui/skeleton";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { MoreVertical, Wrench } from "lucide-react";
import type { MaintenanceRecord } from "@shared/schema";

interface MaintenanceTableProps {
  records: MaintenanceRecord[];
  isLoading: boolean;
  onEdit: (record: MaintenanceRecord) => void;
}

export default function MaintenanceTable({ records, isLoading, onEdit }: MaintenanceTableProps) {
  if (isLoading) {
    return (
      <Card className="glass-effect overflow-hidden">
        <div className="overflow-x-auto">
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Type</TableHead>
                <TableHead>Priority</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Scheduled</TableHead>
                <TableHead>Technician</TableHead>
                <TableHead>Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {Array.from({ length: 5 }).map((_, i) => (
                <TableRow key={i}>
                  <TableCell><Skeleton className="h-4 w-16" /></TableCell>
                  <TableCell><Skeleton className="h-4 w-12" /></TableCell>
                  <TableCell><Skeleton className="h-4 w-16" /></TableCell>
                  <TableCell><Skeleton className="h-4 w-24" /></TableCell>
                  <TableCell><Skeleton className="h-4 w-20" /></TableCell>
                  <TableCell><Skeleton className="h-4 w-8" /></TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </div>
      </Card>
    );
  }

  const getStatusBadge = (status: string) => {
    const variants = {
      scheduled: "outline",
      in_progress: "secondary",
      completed: "default",
      cancelled: "destructive",
    } as const;

    return (
      <Badge variant={variants[status as keyof typeof variants] || "outline"}>
        {status.replace('_', ' ').charAt(0).toUpperCase() + status.replace('_', ' ').slice(1)}
      </Badge>
    );
  };

  const getPriorityBadge = (priority: string) => {
    const variants = {
      low: "outline",
      normal: "secondary",
      high: "destructive",
      urgent: "destructive",
    } as const;

    return (
      <Badge variant={variants[priority as keyof typeof variants] || "outline"}>
        {priority.charAt(0).toUpperCase() + priority.slice(1)}
      </Badge>
    );
  };

  const formatDateTime = (date: Date) => {
    const dateObj = date instanceof Date ? date : new Date(date);
    const now = new Date();
    const isToday = dateObj.toDateString() === now.toDateString();

    if (isToday) {
      return `Today, ${dateObj.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}`;
    }
    return dateObj.toLocaleString();
  };

  if (records.length === 0) {
    return (
      <Card className="glass-effect p-12">
        <div className="text-center">
          <Wrench className="mx-auto mb-4 h-16 w-16 text-muted-foreground" />
          <h3 className="mb-2 text-lg font-semibold">No maintenance records</h3>
          <p className="text-muted-foreground">Maintenance records will appear here when scheduled.</p>
        </div>
      </Card>
    );
  }

  return (
    <Card className="glass-effect overflow-hidden" data-testid="maintenance-table">
      <div className="overflow-x-auto">
        <Table>
          <TableHeader className="bg-muted/50">
            <TableRow>
              <TableHead className="px-6 py-4 text-xs font-semibold uppercase tracking-wider">
                Type
              </TableHead>
              <TableHead className="px-6 py-4 text-xs font-semibold uppercase tracking-wider">
                Priority
              </TableHead>
              <TableHead className="px-6 py-4 text-xs font-semibold uppercase tracking-wider">
                Status
              </TableHead>
              <TableHead className="px-6 py-4 text-xs font-semibold uppercase tracking-wider">
                Scheduled
              </TableHead>
              <TableHead className="px-6 py-4 text-xs font-semibold uppercase tracking-wider">
                Technician
              </TableHead>
              <TableHead className="px-6 py-4 text-xs font-semibold uppercase tracking-wider">
                Cost
              </TableHead>
              <TableHead className="px-6 py-4 text-xs font-semibold uppercase tracking-wider">
                Actions
              </TableHead>
            </TableRow>
          </TableHeader>
          <TableBody className="divide-y divide-border">
            {records.map((record) => (
              <TableRow
                key={record.id}
                className="transition-colors hover:bg-muted/30"
                data-testid={`maintenance-row-${record.id}`}
              >
                <TableCell className="px-6 py-4">
                  <span className="text-sm text-foreground capitalize">
                    {record.type.replace('_', ' ')}
                  </span>
                </TableCell>
                <TableCell className="px-6 py-4">
                  {getPriorityBadge(record.priority)}
                </TableCell>
                <TableCell className="px-6 py-4">
                  {getStatusBadge(record.status)}
                </TableCell>
                <TableCell className="px-6 py-4 text-sm text-muted-foreground">
                  {formatDateTime(record.scheduled_date)}
                </TableCell>
                <TableCell className="px-6 py-4 text-sm text-foreground">
                  {record.technician || "Not assigned"}
                </TableCell>
                <TableCell className="px-6 py-4 text-sm text-foreground">
                  {record.cost ? `$${parseFloat(record.cost).toFixed(2)}` : "-"}
                </TableCell>
                <TableCell className="px-6 py-4">
                  <Button
                    variant="ghost"
                    size="icon"
                    onClick={() => onEdit(record)}
                    data-testid={`edit-maintenance-${record.id}`}
                  >
                    <MoreVertical className="h-4 w-4" />
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </div>
    </Card>
  );
}

client/src/components/orders/OrderDetail.content:
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import {
  Sheet,
  SheetContent,
  SheetDescription,
  SheetHeader,
  SheetTitle,
} from "@/components/ui/sheet";
import { Separator } from "@/components/ui/separator";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { X } from "lucide-react";
import { useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useAuth } from "@/hooks/use-auth";
import { useToast } from "@/hooks/use-toast";
import type { Order } from "@shared/schema";

interface OrderDetailsDrawerProps {
  order: Order;
  onClose: () => void;
}

export default function OrderDetailsDrawer({ order, onClose }: OrderDetailsDrawerProps) {
  const { user } = useAuth();
  const { toast } = useToast();
  const isAdmin = user?.role === "admin";

  const updateStatusMutation = useMutation({
    mutationFn: async (newStatus: string) => {
      await apiRequest("PATCH", `/api/orders/${order.id}/status`, { status: newStatus });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/orders"] });
      toast({
        title: "Status updated",
        description: "Order status has been updated successfully",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Update failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleStatusChange = (newStatus: string) => {
    updateStatusMutation.mutate(newStatus);
  };

  const getStatusBadge = (status: string) => {
    const variants = {
      completed: "default",
      pending: "outline",
      paid: "secondary",
      processing: "secondary", 
      failed: "destructive",
      cancelled: "secondary",
    } as const;

    return (
      <Badge variant={variants[status as keyof typeof variants] || "outline"}>
        {status.charAt(0).toUpperCase() + status.slice(1)}
      </Badge>
    );
  };

  const formatDateTime = (date: Date | null) => {
    if (!date) return "N/A";
    const dateObj = date instanceof Date ? date : new Date(date);
    return dateObj.toLocaleString();
  };

  return (
    <Sheet open={true} onOpenChange={onClose}>
      <SheetContent className="w-full sm:max-w-md" data-testid="order-details-drawer">
        <SheetHeader className="space-y-4">
          <div className="flex items-center justify-between">
            <div>
              <SheetTitle className="text-lg">Order Details</SheetTitle>
              <SheetDescription>#{order.order_number}</SheetDescription>
            </div>
            <Button variant="ghost" size="icon" onClick={onClose} data-testid="close-order-details">
              <X className="h-4 w-4" />
            </Button>
          </div>

          <div className="flex items-center gap-2">
            {getStatusBadge(order.status)}
            <span className="text-sm text-muted-foreground">
              {formatDateTime(order.created_at)}
            </span>
          </div>

          {isAdmin && (
            <div>
              <h3 className="mb-2 text-sm font-semibold text-foreground">Update Status</h3>
              <Select 
                value={order.status} 
                onValueChange={handleStatusChange}
                disabled={updateStatusMutation.isPending}
              >
                <SelectTrigger className="w-full" data-testid="order-status-select">
                  <SelectValue placeholder="Select status" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="pending">Pending</SelectItem>
                  <SelectItem value="paid">Paid</SelectItem>
                  <SelectItem value="processing">Processing</SelectItem>
                  <SelectItem value="completed">Completed</SelectItem>
                  <SelectItem value="failed">Failed</SelectItem>
                  <SelectItem value="cancelled">Cancelled</SelectItem>
                </SelectContent>
              </Select>
            </div>
          )}
        </SheetHeader>

        <div className="mt-6 space-y-6">
          {/* Order Summary */}
          <div>
            <h3 className="mb-3 text-sm font-semibold text-foreground">Order Summary</h3>
            <div className="space-y-2 text-sm">
              <div className="flex justify-between">
                <span className="text-muted-foreground">Items:</span>
                <span className="font-medium">{order.items_count}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-muted-foreground">Payment Method:</span>
                <span className="font-medium">{order.payment_method || "Not specified"}</span>
              </div>
            </div>
          </div>

          <Separator />

          {/* Customer Information */}
          {order.customer_info && typeof order.customer_info === 'object' && (
            <>
              <div>
                <h3 className="mb-3 text-sm font-semibold text-foreground">Customer Information</h3>
                <div className="space-y-2 text-sm">
                  {Object.entries(order.customer_info as Record<string, any>).map(([key, value]) => {
                    // Format address objects
                    if (value && typeof value === 'object' && 'address' in value) {
                      const addr = value as { address?: string; city?: string; state?: string; zip?: string; country?: string };
                      const formattedAddress = [
                        addr.address,
                        addr.city,
                        addr.state,
                        addr.zip,
                        addr.country
                      ].filter(Boolean).join(', ');
                      return (
                        <div key={key} className="flex flex-col gap-1">
                          <span className="text-muted-foreground capitalize">{key.replace(/_/g, ' ')}:</span>
                          <span className="font-medium text-foreground">{formattedAddress || 'N/A'}</span>
                        </div>
                      );
                    }
                    // Render primitive values normally
                    return (
                      <div key={key} className="flex justify-between">
                        <span className="text-muted-foreground capitalize">{key.replace(/_/g, ' ')}:</span>
                        <span className="font-medium">{value?.toString() || 'N/A'}</span>
                      </div>
                    );
                  })}
                </div>
              </div>
              <Separator />
            </>
          )}

          {/* Order Total */}
          <div>
            <h3 className="mb-3 text-sm font-semibold text-foreground">Payment Details</h3>
            <div className="space-y-2 text-sm">
              <div className="flex justify-between font-semibold text-foreground">
                <span>Total Amount:</span>
                <span>${parseFloat(order.total_amount).toFixed(2)}</span>
              </div>
            </div>
          </div>

          <Separator />

          {/* Timeline */}
          <div>
            <h3 className="mb-3 text-sm font-semibold text-foreground">Order Timeline</h3>
            <div className="space-y-3">
              <div className="flex items-start gap-3">
                <div className="mt-1 h-2 w-2 rounded-full bg-blue-500"></div>
                <div className="text-sm">
                  <p className="font-medium">Order Created</p>
                  <p className="text-muted-foreground">{formatDateTime(order.created_at)}</p>
                </div>
              </div>

              {order.updated_at !== order.created_at && (
                <div className="flex items-start gap-3">
                  <div className="mt-1 h-2 w-2 rounded-full bg-green-500"></div>
                  <div className="text-sm">
                    <p className="font-medium">Status Updated</p>
                    <p className="text-muted-foreground">{formatDateTime(order.updated_at)}</p>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </SheetContent>
    </Sheet>
  );
}

client/src/components/orders/OrderFilter.content:
import { Card, CardContent } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Label } from "@/components/ui/label";

interface OrderFiltersProps {
  filters: {
    search: string;
    status: string;
    dateRange: string;
  };
  onFiltersChange: (filters: any) => void;
}

export default function OrderFilters({ filters, onFiltersChange }: OrderFiltersProps) {
  const handleFilterChange = (key: string, value: string) => {
    onFiltersChange({ ...filters, [key]: value });
  };

  return (
    <Card className="glass-effect">
      <CardContent className="p-6">
        <div className="grid grid-cols-1 gap-4 md:grid-cols-3">
          <div>
