            {product.image ? (
              <img
                src={product.image}
                alt={product.name}
                className="h-full w-full object-cover"
              />
            ) : (
              <div className="flex h-full items-center justify-center">
                <Package className="h-16 w-16 text-gray-400" />
              </div>
            )}
          </div>
          <CardContent className="p-4">
            <div className="mb-2 flex items-start justify-between">
              <h3 className="font-semibold text-foreground line-clamp-1" title={product.name}>
                {product.name}
              </h3>
              {getStatusBadge(product.status, product.stock)}
            </div>
            <p className="mb-3 text-sm text-muted-foreground">
              {product.category} â€¢ {product.weight}
            </p>
            <div className="mb-3 flex items-center justify-between">
              <p className="text-lg font-bold text-foreground">
                ${parseFloat(product.price).toFixed(2)}
              </p>
              <p className="text-sm text-muted-foreground">Stock: {product.stock}</p>
            </div>
            {product.specifications && (
              <div className="mb-3 space-y-1 border-t pt-2">
                <p className="text-xs font-semibold text-foreground">Specifications:</p>
                {(product.specifications as any).Dimensions && (
                  <p className="text-xs text-muted-foreground">Dimensions: {String((product.specifications as any).Dimensions)}</p>
                )}
                {(product.specifications as any).Capacity && (
                  <p className="text-xs text-muted-foreground">Capacity: {String((product.specifications as any).Capacity)}</p>
                )}
                {(product.specifications as any)["Power Consumption"] && (
                  <p className="text-xs text-muted-foreground">Power: {String((product.specifications as any)["Power Consumption"])}</p>
                )}
                {(product.specifications as any).Weight && (
                  <p className="text-xs text-muted-foreground">Weight: {String((product.specifications as any).Weight)}</p>
                )}
              </div>
            )}
            <div className="flex gap-2">
              <Button
                variant="outline"
                className="flex-1"
                onClick={() => onEdit(product)}
                data-testid={`edit-product-${product.id}`}
              >
                <Edit className="mr-2 h-4 w-4" />
                Edit
              </Button>
              <Button
                variant="ghost"
                size="icon"
                onClick={() => onDelete(product.id)}
                data-testid={`delete-product-${product.id}`}
              >
                <Trash2 className="h-4 w-4 text-destructive" />
              </Button>
            </div>
          </CardContent>
        </Card>
      ))}
    </div>
  );
}

